INSERT INTO `wp_mgm_templates` (`id`, `name`, `type`, `content`, `create_dt`) VALUES
(1, 'payment_success_title', 'messages', 'Thank You,<br />\r\r\nYour payment has been processed successfully.', '2014-08-10 03:13:59'),
(2, 'payment_success_message', 'messages', '<p>Thank you for your payment. You will be receiving an email shortly to confirm the transaction.</p>\r\r\n<p>Use the menu to the right or click one of the links below to continue.</p>\r\r\n<p>Go to:</p>\r\r\n<p>\r\r\n	<ul>\r\r\n		<li><a href=\\"[home_url]\\">Homepage</a></li>		 \r\r\n		<li><a href=\\"[login_url]\\">Your Profile</a></li>\r\r\n	</ul>\r\r\n</p>	\r\r\n', '2014-08-10 03:14:00'),
(3, 'payment_failed_title', 'messages', 'There was a problem, the payment could not be proccessed.', '2014-08-10 03:14:00'),
(4, 'payment_failed_message', 'messages', '<p>There was a problem with this transaction. You will be receiving an email shortly to confirm the problem.</p>\r\r\n<p>Use the menu to the right or click one of the links below to continue.</p>\r\r\n<p>Go to:</p>\r\r\n<p>\r\r\n	<ul>\r\r\n		<li><a href=\\"[home_url]\\">Homepage</a></li>\r\r\n		<li><a href=\\"[register_url]\\">Register</a></li> \r\r\n	</ul>\r\r\n</p>\r\r\n	\r\r\n', '2014-08-10 03:14:00'),
(5, 'pack_desc_template', 'templates', '[membership_type] - [cost] [currency] per [duration] [duration_period] [num_cycles].<br/>\r\n [if_trial_on]This pack includes a special, limited trial-offer: [trial_cost] [currency] for [trial_duration] [trial_duration_period] [/if_trial_on]', '2014-08-10 03:14:02'),
(6, 'reminder_email_template_subject', 'emails', 'Your Subscription Near Expiration', '2014-08-10 03:14:03'),
(7, 'reminder_email_template_body', 'emails', '<div><h3>Dear [name],</h3></div>\r\n<div>Your account will expire on [expire_date]\r\n<br>This is only a notice, do not be concerned. After your membership expires, when you try to login, a box will appear above the login panel: \\"Your account has expired. To continue using this site, please make a subscription payment to re-activate your account.\\" Simply click \\"subscriptions payment\\" which is hyperlinked and reactivate by choosing what term of membership you want and finally passing through the payment gateway to complete the transaction. You will not loose your user name or access credentials during expiration or the renewal process.</div>', '2014-08-10 03:14:03');
