INSERT INTO `wp_mgm_templates` (`id`, `name`, `type`, `content`, `create_dt`) VALUES
(1, 'payment_success_title', 'messages', 'Thank You,<br />\r\r\nYour payment has been processed successfully.', '2014-08-10 03:13:59'),
(2, 'payment_success_message', 'messages', '<p>Thank you for your payment. You will be receiving an email shortly to confirm the transaction.</p>\r\r\n<p>Use the menu to the right or click one of the links below to continue.</p>\r\r\n<p>Go to:</p>\r\r\n<p>\r\r\n	<ul>\r\r\n		<li><a href=\\"[home_url]\\">Homepage</a></li>		 \r\r\n		<li><a href=\\"[login_url]\\">Your Profile</a></li>\r\r\n	</ul>\r\r\n</p>	\r\r\n', '2014-08-10 03:14:00'),
(3, 'payment_failed_title', 'messages', 'There was a problem, the payment could not be proccessed.', '2014-08-10 03:14:00'),
(4, 'payment_failed_message', 'messages', '<p>There was a problem with this transaction. You will be receiving an email shortly to confirm the problem.</p>\r\r\n<p>Use the menu to the right or click one of the links below to continue.</p>\r\r\n<p>Go to:</p>\r\r\n<p>\r\r\n	<ul>\r\r\n		<li><a href=\\"[home_url]\\">Homepage</a></li>\r\r\n		<li><a href=\\"[register_url]\\">Register</a></li> \r\r\n	</ul>\r\r\n</p>\r\r\n	\r\r\n', '2014-08-10 03:14:00'),
(5, 'pack_desc_template', 'templates', '[membership_type] - [cost] [currency] per [duration] [duration_period] [num_cycles].<br/>\r\n [if_trial_on]This pack includes a special, limited trial-offer: [trial_cost] [currency] for [trial_duration] [trial_duration_period] [/if_trial_on]', '2014-08-10 03:14:02'),
(6, 'reminder_email_template_subject', 'emails', 'Your Subscription Near Expiration', '2014-08-10 03:14:03'),
(7, 'reminder_email_template_body', 'emails', '<div><h3>Dear [name],</h3></div>\r\n<div>Your account will expire on [expire_date]\r\n<br>This is only a notice, do not be concerned. After your membership expires, when you try to login, a box will appear above the login panel: \\"Your account has expired. To continue using this site, please make a subscription payment to re-activate your account.\\" Simply click \\"subscriptions payment\\" which is hyperlinked and reactivate by choosing what term of membership you want and finally passing through the payment gateway to complete the transaction. You will not loose your user name or access credentials during expiration or the renewal process.</div>', '2014-08-10 03:14:03'),
(8, 'private_text', 'messages', 'You need to be logged in to see this part of the content. Please [login] to access.\r\r\n', '2014-08-10 14:21:21'),
(9, 'private_text_template', 'templates', '<div style=\\"border-style:solid; border-width:1px; margin-bottom:1em; background-color:#E4F2FD; border-color:#C6D9E9; margin:5px; font-family:\\''Lucida Grande\\'',\\''Lucida Sans Unicode\\'',Tahoma,Verdana,sans-serif; font-size:13px; color:#333333;\\">\r\r\n	<div style=\\"margin: 5px 10px;\\">[message]</div>\r\r\n</div>', '2014-08-10 14:21:21'),
(10, 'register_form_row_template', 'templates', '<div class=\\"[user_field_wrapper]\\">\r\r\n	<label>[user_field_label]</label>	\r\r\n	<div>[user_field_element]</div>\r\r\n</div>	', '2014-09-11 11:25:15'),
(11, 'register_form_row_autoresponder_template', 'templates', '<div class=\\"[user_field_wrapper]\\">\r\r\n	<div>[user_field_element] &nbsp; <label>[user_field_label]</label></div>\r\r\n</div>	', '2014-09-11 11:25:15'),
(12, 'pack_desc_lifetime_template', 'templates', '[membership_type] - [cost] [currency] for Lifetime', '2014-09-11 11:41:17'),
(13, 'subs_intro', 'messages', 'After pressing the register button you will be asked to make payment for your registration.', '2014-09-11 11:53:26'),
(14, 'tos', 'messages', 'Terms &amp; Conditions<br>', '2014-09-11 11:53:26'),
(15, 'private_text_no_access', 'messages', 'Sorry but you do not have access to this content.', '2014-09-11 11:53:26'),
(16, 'private_text_purchasable', 'messages', 'This content is available for purchase. Please use the \\"Buy Now\\" button below to purchase the content.', '2014-09-11 11:53:26'),
(17, 'private_text_purchasable_login', 'messages', 'This content is available for purchase. Please select from available options.<br />\r\r\n[purchase_options]', '2014-09-11 11:53:26'),
(18, 'private_text_purchasable_pack_login', 'messages', 'This Pack is available for purchase. Please select from available options.<br />\r\r\n[purchase_options]', '2014-09-11 11:53:26'),
(19, 'private_text_postdelay_no_access', 'messages', 'Sorry,this post is not avaliable now , it will avalible after some time.', '2014-09-11 11:53:26'),
(20, 'text_guest_purchase_pre_button', 'messages', '<b>You are about to buy \\"[post_title]\\" as Guest for [purchase_cost].</b><br/>\r\r\n<b>Members who can access the content without purchasing separately,</b><br/><br>\r\r\n[membership_types]<br />\r\r\n<em>Please use the Buy button below to buy and instantly access the post.</em><br/><br>', '2014-09-11 11:53:26'),
(21, 'text_guest_purchase_pre_register', 'messages', '<b>You are about to register and buy \\"[post_title]\\" thereafter and the post price is [purchase_cost].</b><br/>\r\r\n<b>Members who can access the content without purchasing separately,</b><br/><br>\r\r\n[membership_types]<br />', '2014-09-11 11:53:26'),
(22, 'login_errmsg_null', 'messages', 'Your account is not active. To activate your account please complete the <a href=\\"[subscription_url]\\">subscription payment</a>.', '2014-09-11 11:53:26'),
(23, 'login_errmsg_expired', 'messages', 'Your account has expired. Please make a new <a href=\\"[subscription_url]\\">subscription payment</a> to re-activate your account.', '2014-09-11 11:53:26'),
(24, 'login_errmsg_date_range', 'messages', 'Your membership starts from [start_date] to [end_date], you can only access the member section between the dates specified. Please make a new <a href=\\"[subscription_url]\\">subscription payment</a> to re-activate your account.', '2014-09-11 11:53:26'),
(25, 'login_errmsg_trial_expired', 'messages', 'Your trial account has expired. To continue using this site, please make a <a href=\\"[subscription_url]\\">subscription payment</a> to re-activate your account.', '2014-09-11 11:53:26'),
(26, 'login_errmsg_pending', 'messages', 'Your account is now PENDING for activation!', '2014-09-11 11:53:26'),
(27, 'login_errmsg_default', 'messages', 'Your account is not active.', '2014-09-11 11:53:26'),
(28, 'login_errmsg_cancelled', 'messages', 'Your subscription has been CANCELLED!<br /> \r\r\nPlease make a new <a href=\\"[subscription_url]\\">subscription payment</a> to re-activate your account.', '2014-09-11 11:53:26'),
(29, 'ppp_pack_template', 'templates', '<div>\r\n	<div><h3>[pack_name] - [pack_cost] [pack_currency]</h3></div>\r\n	<div>[pack_description]</div><div>[pack_posts]</div><div>[pack_buy_button]</div>\r\n</div>', '2014-09-11 11:53:26'),
(30, 'pack_desc_date_range_template', 'templates', '[membership_type] - [cost] [currency] starts from [pack_start_date]  to [pack_end_date]', '2014-09-11 11:53:26'),
(31, 'profile_form_row_template', 'templates', '<div class=\\"[user_field_wrapper]\\">\r\r\n	<label>[user_field_label]</label>	\r\r\n	<div>[user_field_element]</div>\r\r\n</div>	', '2014-09-11 11:53:26'),
(32, 'registration_email_template_subject', 'emails', 'Your Account Created', '2014-09-11 11:53:31'),
(33, 'registration_email_template_body', 'emails', '<div><h3>Dear [name],</h3></div>\r\n<div>Your account created<br> Username: [username]<br>Password: [password]<br> Login here [login_url]</div>', '2014-09-11 11:53:31'),
(34, 'new_user_notification_email_template_subject', 'emails', '[blogname] New User Registration', '2014-09-11 11:53:31'),
(35, 'new_user_notification_email_template_body', 'emails', '<div>\r\n	<h4>Dear Admin,</h4>\r\n</div>\r\n<div>\r\n	New user registration on your blog [blogname]<br> \r\n	Username : [username]<br>\r\n	E-mail : [email]<br> \r\n	Membership Type : [membership_type]<br> \r\n	Membership Amount : [amount]\r\n</div>', '2014-09-11 11:53:31'),
(36, 'user_upgrade_notification_email_template_subject', 'emails', 'User upgraded on your blog [blogname]', '2014-09-11 11:53:31'),
(37, 'user_upgrade_notification_email_template_body', 'emails', '<div>\r\n	<h4>Dear Admin,</h4>\r\n</div>\r\n<div>\r\n	User upgraded on your blog [blogname]<br> \r\n	Username : [username]<br>\r\n	E-mail : [email]<br> \r\n	Membership Type : [membership_type]<br> \r\n	Membership Amount : [amount]\r\n</div>', '2014-09-11 11:53:31'),
(38, 'payment_success_email_template_subject', 'emails', '[blogname] Payment Receipt', '2014-09-11 11:53:31'),
(39, 'payment_success_email_template_body', 'emails', 'This is an automatic notification from [blogname] to [name] ([email]).<br>\r\nThis is a notification to inform you that your payment was successful and the post you purchased is now available to view.<br>\r\nFor more information please contact [admin_email].', '2014-09-11 11:53:31'),
(40, 'payment_success_subscription_email_template_body', 'emails', 'This is an automatic notification from [blogname] to [name] ([email]). <br>\r\nThis is a notification to inform you that your payment was successful for [payment_type] of [subscription]. <br>\r\nFor more information please contact [admin_email].', '2014-09-11 11:53:31'),
(41, 'payment_failed_email_template_subject', 'emails', '[blogname] Payment Failed', '2014-09-11 11:53:31'),
(42, 'payment_failed_email_template_body', 'emails', 'This is an automatic notification from [blogname] to [name] ([email]).<br>\r\nThis is a notification to inform you that your [payment_type] failed for the following reason: [reason].<br>\r\nFor more information please contact [admin_email].', '2014-09-11 11:53:31'),
(43, 'payment_active_email_template_subject', 'emails', '[blogname] Subscription Activated', '2014-09-11 11:53:31'),
(44, 'payment_active_email_template_body', 'emails', 'This is an automatic notification from [blogname] to [name] ([email]).<br>\r\nThis is a notification to inform you that your Subscription has been activated.<br>\r\nFor more information please contact [admin_email].', '2014-09-11 11:53:32'),
(45, 'payment_pending_email_template_subject', 'emails', '[blogname] Payment Receipt - Status Pending', '2014-09-11 11:53:32'),
(46, 'payment_pending_email_template_body', 'emails', 'This is an automatic notification from [blogname] to [name] ([email]).<br>\r\nThis is a notification to inform you that your payment was received in pending status. Status: [reason].<br>\r\nFor more information please contact [admin_email].', '2014-09-11 11:53:32'),
(47, 'payment_error_email_template_subject', 'emails', '[blogname] Error on Payment', '2014-09-11 11:53:32'),
(48, 'payment_error_email_template_body', 'emails', 'This is an automatic notification from [blogname] to [name] ([email]).<br>\r\nThis is a notification to inform you that an error occurred while processing your payment.<br>\r\nDetails: [reason].<br>\r\nFor more information please contact [admin_email].', '2014-09-11 11:53:32'),
