(53, 'retrieve_password_email_template_subject', 'emails', '[blogname] Password Reset', '2014-09-11 11:53:32'),
(54, 'retrieve_password_email_template_body', 'emails', 'Someone has asked to reset the password for the following site and username.<br>\r\n[siteurl]<br>\r\nUsername: [username]<br>\r\nTo reset your password visit the following address, otherwise just ignore this email and nothing will happen.<br>\r\n<a target=\\"_blank\\" href=\\"[passwordlink]\\">[passwordlink]</a>', '2014-09-11 11:53:32'),
