(535, '_transient_mgm_current_version', 'Current version: 1.8.43 <br>Upgrade available to version 1.8.45. <a href="javascript:load_upgrade_url(''1.8.45'')" title="Upgrade now">Upgrade</a> now. <iframe id="upgrade_ifr" allowtransparency="true" width="0" height="0" frameborder="0" src="#" scrolling="no" style="display:none"></iframe>\r\n						   <script language="javascript">\r\n							// load\r\n							load_upgrade_url = function(v){\r\n								// check\r\n								if(typeof mgm_set_tab_url == "function"){	\r\n									// output\r\n									jQuery("#upgrade_ifr").attr("src","http://www.magicmembers.com/wp-content/plugins/mgms/mgms.php?action=upgrade_screen&product_id=3&product_name=Unlimited+License&product_brand=magic-members&version=1.8.43&host=http%3A%2F%2Freqrap&build=2.8.0&stage=dev&email=james%40billson.com&license=afe3e5cd2d7d70688678af3f1f940bc8").attr("width", "400px").attr("height","150px").show();	\r\n								}else{\r\n									window.location="admin.php?page=mgm/admin&tabs=admin_home,upgrade&new_version=1.8.45";\r\n								}						\r\n							}\r\n						 </script>', 'no'),
